INSERT INTO `kategorija` (`id`, `naziv`, `opis`, `link`) VALUES
(1, 'Aktuelno', 'Ponude koje su aktuelne!', ''),
(2, 'Višednevni izleti', 'Višednevni izleti', 'media/images/pozadine/UVAC%20POZADINA.jpg'),
(3, 'Produženi vikend', 'Produženi vikend', ''),
(4, 'Jednodnevni izleti', 'Jednodnevni izleti', 'media/images/pozadine/VISEGRAD%20MOST.jpg'),
(5, 'Vjerski turizam', 'Turistička agencija Tavor Pale organzuje obilaske vjerskih objekata širom Bosne i Hercegovine, Crne Gore i Srbije. Manastir Ostrog, Manastir Studenica, Manastir Žiča, Sveta Gora...', 'media/images/pozadine/manastir-hilandar-vjerski-turizam-tavor-pale.jpg'),
(6, 'Avanturistički turizam', 'Aktivni turizam', 'media/images/pozadine/GIF%20VODOPAD.gif'),
(7, 'Obilazak prirodnih resursa', 'Obilazak prirodnih resursa', ''),
(8, 'Sportsko rekreativni turizam', 'Sportsko rekreativni turizam', ''),
(9, 'Sajmovi i konferencijski turizam', 'Sajmovi i konferencijski turizam', ''),
(10, 'Istorijski spomenici', 'Istorijski spomenici', ''),
(11, 'Gastro turizam', 'Gastro turizam', ''),
(12, 'Zimski turizam', 'Zimski turizam', ''),
(13, 'Ljetni turizam', 'Ljetni turizam', 'media/images/pozadine/ljetovanje-turisticka-agencija-tavor-pale.jpg'),
(14, 'Obilazak kultura i regija', 'Obilazak kultura i regija', ''),
(15, 'Legende i predanja', 'Legende i predanja', ''),
(17, 'Vikend Putovanje', '', ''),
(18, 'Prevoz', 'Usluge prevoza na različite lokacije', ''),
(19, 'Aktivni odmor', 'Ukoliko ste umorni od svakodnevnih obaveza, stresnih situacija, pritiska na poslu, nemate strpljenja i želite da odete u prirodu, da vidite ljepote prirode, da osetite promjenu u odnosu prema klasičnom odmoru, turistička agencija „TAVOR“ je pripremila posebne sedmodnevne i desetodnevne (ili kraće) kombinacije povezanih specijalnih programa eko, etno i aktivnog turizma na teritoriji tri države : Srbije, BiH – Republike Srpske i Crne Gore koje su prepune mogućnosti za kvalitetne vidove aktivnog i avanturističkog turizma radi obnavljanja vlastite energije klijenata željnih uzbuđenja, izazova, neočekivanih doživljaja, zabave, novih iskustava i saznanja.', 'media/images/Aktivni%20odmor/tavor%20rusi%202.jpg'),
(21, 'Aktuelne ponude', 'Ponude koje su trenutno aktuelne', '0'),
(22, 'Nova godina', '', 'media/images/nova%20godina.png'),
(23, 'Škola u prirodi', 'Škola u prirodi je poseban vid kreativnog, nastavnog i obrazovnog rada. Smještaj se organizuje van mjesta stanovanja, u prirodnoj sredini, uz prošireno pedagoško djelovanje. Nastavno-obrazovni rad povezuje se sa psihofizičkom rekreacijom u prirodi. Realizacija nastavnih sadržaja, utvrđenih nastavnim planom i programom, prilagođava se konkretnim uslovima prirodne i lokalne sredine. Obrađuju se oni sadržaji kojima ti uslovi najviše odgovaraju i u kojima se oni mogu najuspešnije ostvariti. Učenici su  pod stručnim rukovodstvom nastavnog i vannastavnog kadra agencije, gdje im se  provjerava znanje i iskustvo stečeno u učionicama kroz primjenu u stvarnoj životnoj sredini. Ovde oni, imaju priliku da se zbliže i bolje upoznaju pod novim okolnostima, da dogovore i usvoje pravila zajedničkog suživota, vježbaju toleranciju i uvažavanje različitosti, utvrde pozitivne higijenske navike i pravila lijepog ponašanja, a nađu vremana i za zabavu, za koju su zaduženi naši animatori. Oni svaku veče organiziraju zajedničke igre i nezaobilazne zabavne programe … ', 'media/images/Skola%20u%20prirodi/SLIKA_000000.jpeg'),
(24, 'Zimovanje za djecu', '', 'media/images/pozadine/Zimovanje%20djeca.png'),
(25, 'Zimovanje', '', 'media/images/pozadine/idila.jpg'),
(26, 'Manifestacije', '', '0'),
(27, 'Dječiji odmor u Grčkoj - ljeto 2014', 'Pozivamo djecu svih uzrasta da učestvuju u međunarodnim sportskim kampovima u ljetnjem periodu na Halkidikiju, na Peloponezu, u regionu Atine (Atika), na ostrvu Tasosu i na svim ostalim popularnim regijama Grčke. Svi kampovi su ukusno uređeni i opremljeni sa svim modernim sadržajima i dobro organizovani sa mnogobrojnim opcijama za sport i rekreaciju za djecu svih starosnih grupa (predškolske i školske).  Kampovi takođe posjeduju ishranu vrhunskog kvaliteta na bazi domaće kuhinje, raznovrsne zabavne programe, učenje stranog jezika i naravno učenje plivanja i provoda mnogo vremena na najboljim plažama u regionu Grčke.', 'media/images/Skola%20u%20prirodi/Kampovi/LJETNI%20KAMPOVI%20GRCKA.jpg');
